package com.example.amangeldi_abdibaitov_mo3_hw_4


import android.view.View
import android.os.Bundle
import android.view.ViewGroup
import android.view.LayoutInflater
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.amangeldi_abdibaitov_mo3_hw_4.databinding.FragmentContinentsBinding

class ContinentsFragment : Fragment(), OnItemClickListener {
    private lateinit var binding: FragmentContinentsBinding
    private var continentsList = ArrayList<ContinentsModel>()
    private var adapter = ContinentsAdapter(continentsList, this)

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        binding = FragmentContinentsBinding.inflate(inflater)
        loadData()
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        initAdapter()
    }

    private fun initAdapter() = with(binding) {
        rvContinents.layoutManager = LinearLayoutManager(requireContext())
        rvContinents.adapter = adapter
    }

    private fun loadData() {
        continentsList.add(
            ContinentsModel(
                "Евразия",
                "Евра́зия — самый большой материк на Земле и единственный, омываемый четырьмя океанами: на юге — Индийским, на севере — Северным Ледовитым, на западе — Атлантическим, на востоке — Тихим.",
                "data:image/jpeg;base64,"
            )
        )
        continentsList.add(
            ContinentsModel(
                "Северная Америка",
                "Се́верная Аме́рика — один из континентов планеты Земля, расположенный на севере Западного полушария Земли. Северная Америка омывается с запада Тихим океаном с Беринговым морем, заливами Аляска и Калифорнийским, с востока Атлантическим океаном с морями Лабрадор, Карибским, заливом Святого Лаврентия и Мексиканским, с севера — Северным Ледовитым океаном с морями Бофорта, Баффина, Гренландским и Гудзоновым заливом.",
                "data:image/jpeg;base64,"
            )
        )
        continentsList.add(
            ContinentsModel(
                "Южная Америка",
                "Ю́жная Аме́рика — южный континент в Америке, расположенный в основном в Западном и Южном полушариях планеты Земля, тем не менее, частично континент располагается и в Северном полушарии. Омывается на западе Тихим океаном, на востоке — Атлантическим, с севера ограничивается Северной Америкой, граница между Америками проходит по Панамскому перешейку и Карибскому морю.",
                "data:image/jpeg;base64,"
            )
        )
        continentsList.add(
            ContinentsModel(
                "Африка",
                "А́фрика — второй по площади материк после Евразии, омываемый Средиземным морем с севера, Красным — с северо-востока, Атлантическим океаном с запада и Индийским океаном с востока и юга. Африкой называется также часть света, состоящая из материка Африка и прилегающих островов.",
                "data:image/jpeg;base64,"
            )
        )
        continentsList.add(
            ContinentsModel(
                "Австралия и Океания",
                "Австра́лия и Океа́ния — часть света, состоящая из материка Австралия, островов, прилегающих к Австралии, и островов, входящих в Океанию.",
                "data:image/jpeg;base64,"
            )
        )
    }

    override fun onItemClick(position: Int) {
        val continent = continentsList[position].title
        val bundle = Bundle()
        bundle.putString("continents", continent)
        val countryFragment = CountryFragment()
        countryFragment.arguments = bundle
        requireActivity().supportFragmentManager.beginTransaction()
            .replace(R.id.fl_mine, countryFragment).addToBackStack(null).commit()
    }
}